}

impl FunctionMetrics {
    pub fn new() -> Self {
        Self {
            total_calls: 0,
            successful_calls: 0,
            failed_calls: 0,
            total_cost: 0.0,
            total_duration_ms: 0,
            average_duration_ms: 0.0,
        }
    }

    pub fn record_action(&mut self, action: &Action) {
        self.total_calls += 1;
        self.total_cost += action.cost.unwrap_or(0.0);
        self.total_duration_ms += action.duration_ms.unwrap_or(0);

