    /// Record a capability call in the causal chain.
    pub fn log_capability_call(
        &mut self,
        plan_id: &PlanId,
        intent_id: &IntentId,
        capability_id: &CapabilityId,
        function_name: &str,
        args: Vec<Value>,
    ) -> Result<Action, RuntimeError> {
